mapper.select:
 		select m_no, userid, username,userpwd, nickname, m_pic, tel, email, to_char(regdate, 'YYYY-MM-DD') regdate, regcode 
 		from(
 			select ROWNUM RN, A.*
 							from(
 									select * 
 									from register
 									)A
 							)
		where RN between #{start} and #{end}

 		select m_no, userid, username,userpwd, nickname, m_pic, tel, email, to_char(regdate, 'YYYY-MM-DD') regdate, regcode
 		from(
 			select ROWNUM RN, A.*
 							from(
 									select * 
 									from register


							   				order by userid


							   				order by email


							   				order by username


							   				order by regdate desc


 									)A
 							)
		where RN between #{start} and #{end}

 		select m_no, userid, username,userpwd, nickname, m_pic, tel, email, to_char(regdate, 'YYYY-MM-DD') regdate, regcode
 		from(
 			select ROWNUM RN, A.*
 							from(
 									select * 
 									from register



								 				userid LIKE '%'||#{key}||'%'


								 				username LIKE '%'||#{key}||'%'


								 				 email LIKE '%'||#{key}||'%'


								 				 tel LIKE '%'||#{key}||'%'



 									)A
 							)
		where RN between #{start} and #{end}
